// API utility functions
export const getServerUrl = () => {
  // Use environment variable if set, otherwise determine based on hostname
  if (process.env.REACT_APP_SERVER_URL) {
    return process.env.REACT_APP_SERVER_URL;
  }
  
  // For development (localhost)
  if (window.location.hostname === 'localhost') {
    return 'http://localhost:3001';
  }
  
  // For production - replace YOUR_RAILWAY_URL with your actual Railway URL
  return 'https://YOUR_RAILWAY_URL.railway.app';
};

export const getApiUrl = (endpoint) => {
  return `${getServerUrl()}${endpoint}`;
};
